import React, { useState } from 'react'
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
const KlatterForm = () => {

    const [volume, setVolume] = useState(""); 
    const [month, setMonth] = useState("");
    const [year, setYear] = useState(""); 
    const [issue, setIssue] = useState("");

    const onSubmitHandler = (e) => {

        e.preventDefault();

        axios.post('http://localhost:8000/api/klatter', {
            volume,
            month,
            year,
            issue   
        })
            .then(res=>{
            console.log(res);
            console.log(res.data);
        })
            .catch(err=>console.log(err))
    }

    return (
        <div>
            <form onSubmit={onSubmitHandler}>
                <p>
                    <label>Volume</label><br/>
                    <input type="text" onChange = {(e)=>setVolume(e.target.value)}/>
                </p>
                <p>
                    <label>Month</label><br/>
                    <input type="text" onChange = {(e)=>setMonth(e.target.value)}/>
                </p>
                <p>
                    <label>Year</label><br/>
                    <input type="text" onChange = {(e)=>setYear(e.target.value)}/>
                </p>
                <p>
                    <label>Issue</label><br/>
                    <input type="text" onChange = {(e)=>setIssue(e.target.value)}/>
                </p>
                <input type="submit"/>
            </form>
        </div>
    )
}
export default KlatterForm;
